function str=conn_strexpand(varargin)
global CONN_gui
if nargin<1, str={}; return; end
str=varargin{1};
changed=false(size(str));
for n1=2:nargin
    for n2=1:min(numel(str),numel(varargin{n1}))
        if ~isempty(varargin{n1}{n2}), 
            if ~isfield(CONN_gui,'isjava')||CONN_gui.isjava, changed(n2)=true;  str{n2}=[str{n2} ' <i>(' varargin{n1}{n2} ')</i>']; 
            else str{n2}=[str{n2} ' (' varargin{n1}{n2} ')']; 
            end
        end
    end
end
for n2=find(changed(:))'
    str{n2}=['<HTML>' str{n2} '</HTML>'];
end
end

function str=conn_longcontrastname(names,c)
str='';
n=find(c~=0);
if numel(n)==1&&c(n)==1
    str=names{n};
else
    for n=n(:)'
        str=strcat(str,regexprep(names{n},{'^(.*)',' \+1 \*',' \-1 \*'},{sprintf(' %+g * $1',c(n)),' +',' -'}));
    end
end
end

