        else
            txt1={sprintf('Subject %d Session %d: %d scans', xlsub(pos(1),pos(2)), xlses(pos(1),pos(2)),xlscn(pos(1),pos(2)))};
        end
    else
        if isnan(xlval(pos(1),pos(2)))
            txt1={' '};
