            end
            txt1={sprintf('Subject %d Session %d', xlsub(pos(1),pos(2)), xlses(pos(1),pos(2))),sprintf('Condition %s %s during scan %d',CONN_x.Setup.conditions.names{xlcon(pos(1),pos(2))}, offon, xlscn(pos(1),pos(2))),' (click to select)'};
